# Draws digit 0 in the colour specified by colorNo with font height h 
# from the current turtle position. 
def digit0(h, colorNo) :
    turtle.penup()
    col4910b11b = turtle.pencolor(); turtle.pencolor("#000000")
    turtle.forward(h/4.0)
    turtle.pendown()
    turtle.pencolor(col4910b11b)
    turtle.right(45)
    len = h/math.sqrt(2)
    turtle.forward(len, colorNo)
    turtle.penup()
    cole256f86f = turtle.pencolor(); turtle.pencolor("#000000")
    turtle.backward(len)
    turtle.left(45)
    turtle.backward(h/4.0)
    turtle.pencolor(cole256f86f)
    letterO(h, colorNo)

